import React, { useState } from "react";

import "./ExpensesFilter.css";

const ExpensesFilter = () => {
  const { enteredYearFilter, setEnteredYearFilter } = useState("");

  const onSelectHandler = (e) => {
    setEnteredYearFilter(e.target.value);
    console.log(enteredYearFilter);
  };

  return (
    <div className="expenses-filter">
      <div className="expenses-filter__control">
        <label>Filter by year</label>
        <select>
          <option value="2022" onClick={onSelectHandler}>
            2022
          </option>
          <option value="2021" onClick={onSelectHandler}>
            2021
          </option>
          <option value="2020" onClick={onSelectHandler}>
            2020
          </option>
          <option value="2019" onClick={onSelectHandler}>
            2019
          </option>
        </select>
      </div>
    </div>
  );
};

export default ExpensesFilter;
